import React from 'react'
import styles from './VictoryBarComponent.module.css'
import { ChartProps } from '../../../App'
import Label from '../../UI/Label'
import { VictoryBar, VictoryChart, VictoryTooltip, VictoryZoomContainer } from 'victory';

interface VictoryBarProps{
  data: {x: any, y: any}[],
  title: string
  x?: string
  y?: string
}

const VictoryBarComponent = (props: VictoryBarProps) => {
  return (
    <div className={styles.chartBox}>
    <div className={styles.chartBoxSubcontainer}>
        <Label text={props.title} className={styles.chartTitleLabel} />
        <VictoryChart 
        domainPadding={{x: 10}}
        containerComponent={
          <VictoryZoomContainer/>
        }
      >

                <VictoryBar
                    style={{
                        data: { fill: "tomato"},

                    }}
                    barRatio={0.5}
                    labels={({ datum }) => `${props.y ? props.y : "y"}: ${datum.y}\n ${props.x ? props.x : "x"}: ${datum.x}`}
                    labelComponent={<VictoryTooltip />}
                    //data={houseMedianAgeChartsData.slice(0, 100)}
                    data={props.data}
                    // data accessor for x values
                    y="y"
                    x="x"

                // data accessor for y values
                //y="name"
                />
            </VictoryChart>
    </div>
</div>
  )
}

export default VictoryBarComponent
