import React from 'react'
import styles from './VictoryAreaChart.module.css'
import { ChartProps } from '../../../App'
import Label from '../../UI/Label'
import { VictoryArea, VictoryBar, VictoryBrushContainer, VictoryChart, VictoryTheme, VictoryTooltip, VictoryZoomContainer } from 'victory';
import { VictoryChartProps } from '../Victory';


const VictoryAreaChart = (props: VictoryChartProps) => {
  return (
    <div className={styles.chartBox}>
    <div className={styles.chartBoxSubcontainer}>
        <Label text={props.title} className={styles.chartTitleLabel} />
        <VictoryChart

        domainPadding={{x: 10}}
        containerComponent={
            <VictoryBrushContainer
              brushDimension="x"
              brushDomain={{x: [0.1, 0.3]}}
            />
          }

      >

                <VictoryArea
                    style={{
                        data: { fill: "tomato", opacity: 0.7, stroke: "tomato"},

                    }}


                // data accessor for y values
                //y="name"
                />
            </VictoryChart>
    </div>
</div>
  )
}

export default VictoryAreaChart
